import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor(private router: Router) { }

}
<mat-toolbar>

  <mat-toolbar-row>
    <a [routerLink]="['/']">Bodegas de Mendoza</a>
  </mat-toolbar-row>

  <mat-toolbar-row>
    <a [routerLink]="['form']">Agregar Bodega</a>
  </mat-toolbar-row>

  <mat-toolbar-row>
    <a [routerLink]="['delete']">Eliminar Bodega</a>
  </mat-toolbar-row>

</mat-toolbar>
