import { Component, OnInit } from '@angular/core';
i//mport { BodegaService } from '../bodegas/services/bodegas.services';
import { BodegasComponent } from '../bodegas/bodegas.component';
//import { Bodega } from '../bodegas/interfaces/bodegas.interfaces';
import { FormsModule } from '@angular/forms';
import { WineryService } from 'src/app/services/winery.service';
import { Winery } from 'src/app/model/winery';


@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit{
  winery: Winery = {
    _id: 0,
    name:'',
    zone:''
  }

  onSubmit() {
    this.wineryService.saveWinery(this.winery).subscribe()
    console.log(this.winery);
    this.winery = {
      _id:0,
      name: '',
      zone: ''
    };
  }


  constructor(private wineryService: WineryService) { }

  ngOnInit(): void {
this.addBodega();
  }

  addBodega(): void{
    this.wineryService.saveWinery(this.winery)
  }
}
